import { GET_BREEDS } from "./Actions";

const initialState = {
  loading: false,
  filteredBreeds: [],
  breedsLoaded: [],
  breedDetail: {},
};

function rootReducer(state = initialState, action) {
  if (action.type === GET_BREEDS) {
    return {
      ...state,
      breedsLoaded: [1,3,5,'andres'],
    };
  }
}

export default rootReducer;
