import React, { useState, useEffect } from "react";
import axios from "axios";
import S from "./CreateBreed.module.css";
function CreateBreed() {
  const [Breed, setBreed] = useState({
    name: "",
    height: "",
    weight: "",
    life_span: "",
    img: "",
    temperamentos: [],
  });
  const [Temps, setTemps] = useState([]);
  const [temperaments, setTemperaments] = useState();

  useEffect(() => {
    axios
      .get("http://localhost:3001/temperaments")
      .then((r) => r.data)
      .then((e) => setTemperaments(e));
  }, []);

  const handleSelector = (e) => {
    setTemps(...Temps, e.target.value);
    setBreed({
      ...Breed,
      temperamentos: temperaments.concat(e.target.value)
    })
    // Breed.temperaments.push(e.target.value);
    console.log(e.target.value);
  };

  const handleChange = (e) => {
    setBreed({
      ...Breed,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div>
      <div className={S.container}>
        <form>
          <input
            required
            name="name"
            type="text"
            placeholder="Name"
            autoComplete="off"
            onChange={handleChange}
          />
          <input
            required
            name="weight"
            type="number"
            placeholder="Weight"
            onChange={handleChange}
            min="1"
          />
          <input
            required
            name="height"
            type="number"
            placeholder="Height"
            onChange={handleChange}
            min="1"
          />
          <input
            required
            name="life_span"
            type="number"
            placeholder="Life span"
            onChange={handleChange}
            min="1"
          />
          <input
            required
            name="img"
            type="link"
            placeholder="Image Link/Url"
            onChange={handleChange}
            min="1"
          />
          <input type="submit"></input>
        </form>
        <select onChange={handleSelector} name="temperament" id="temperaments">
          {temperaments?.map((e) => (
            <option name="" key={e.id} value={e.id}>
              {e.name}
            </option>
          ))}
        </select>
        <ul>
          {Breed.temperamentos?.map((e) => (
            <li key={e}>{e}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default CreateBreed;
