import React, { useState, useEffect} from "react";
import S from "./SideBar.module.css";
import axios from "axios";
import { useDispatch } from "react-redux";
import { searchBreed } from "../../Redux/Actions";

function SideBar() {
  const dispatch = useDispatch();
  const [breed, setBreed] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:3001/temperaments")
      .then((r) => r.data)
      .then((e) => setTemperaments(e));
  }, []);

  const search = (e) => {
    e.preventDefault();
    dispatch(searchBreed(breed));
    setBreed('');
  };

  return (
    <div className={S.container}>
      <div className={S.search}>
        <label>Search a breed</label>
        <form>
          <input
            type="text"
            placeholder="Breed..."
            onChange={(e) => {
              setBreed(e.target.value);
            }}
            value={breed}
            className={S.breed}
            autoComplete="off"
          />
          <button className={S.button} onClick={search}>
            Search
          </button>
        </form>
      </div>

      <div className={S.filter}>
        <div className={S.sort}>
          <h2>Sort By</h2>
          <select name="order" className={S.comboBox}>
            <option value="name">name</option>
            <option value="weight">weight</option>
          </select>
        </div>
        <div className={S.sort}>
          <h2>Filter</h2>
          <select name="temperament" className={S.comboBox}>
            <option value="">all </option>
          </select>
          <select name="Origin" className={S.comboBox}>
            <option value="all">From everywhere</option>
            <option value="api">From Api</option>
            <option value="bd">From Data Base</option>
          </select>
        </div>
      </div>
    </div>
  );
}
export default SideBar;
