import React, { useState, useEffect } from "react";
import axios from "axios";
import S from "./CreateBreed.module.css";
function CreateBreed() {
  const [Breed, setBreed] = useState({
    name: "",
    height: "",
    weight: "",
    life_span: "",
    img: "",
    temperaments: [],
  });
  const [temperaments, setTemperaments] = useState();
  const [name] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/temperaments")
      .then((r) => r.data)
      .then((e) => setTemperaments(e));
  }, []);

  const handleSelector = (e) => {
    let temp = Breed.temperaments;
    temp.push(e.target.value);
    // eslint-disable-next-line eqeqeq
    let a = temperaments.filter((i) => i.id == e.target.value);
    name.push(a[0].name);
    setBreed({
      ...Breed,
      [e.target.name]: temp,
    });
    // Breed.temperaments.push(e.target.value)
  };

  const handleChange = (e) => {
    setBreed({
      ...Breed,
      [e.target.name]: e.target.value,
    });
  };

  const clear = ()=>{
    setBreed({
      name: "",
      height: "",
      weight: "",
      life_span: "",
      img: "",
      temperaments: [],
    });
  }

  return (
    <div>
      <div className={S.container}>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            axios.post("http://localhost:3001/dog", {
              name: Breed.name,
              height: Breed.height,
              weight: Breed.weight,
              life_span: Breed.life_span,
              image: Breed.img,
              temperaments: Breed.temperaments,
            });
          }}
        >
          <input
            required
            value={Breed.name}
            name="name"
            type="text"
            placeholder="Name"
            autoComplete="off"
            onChange={handleChange}
          />
          <input
            required
            name="weight"
            type="number"
            placeholder="Weight"
            value={Breed.weight}
            onChange={handleChange}
            min="1"
          />
          <input
            required
            name="height"
            type="number"
            placeholder="Height"
            onChange={handleChange}
            min="1"
          />
          <input
            required
            name="life_span"
            type="number"
            placeholder="Life span"
            onChange={handleChange}
            min="1"
          />
          <input
            required
            name="img"
            type="link"
            autoComplete="off"
            placeholder="Image Link/Url"
            onChange={handleChange}
            min="1"
          />
          <input type="submit"></input>
        </form>
        <select onChange={handleSelector} name="temperament" id="temperaments">
          {temperaments?.map((e) => (
            <option name="temperaments" key={e.id} value={e.id}>
              {e.name}
            </option>
          ))}
        </select>
        <ul>
          {name?.map((e) => (
            <li key={Math.random(1)}>{e}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default CreateBreed;
