import React, { useState, useEffect } from "react";
import axios from "axios";
import S from "./CreateBreed.module.css";
function CreateBreed() {
  const [Breed, setBreed] = useState({
    name: "",
    min_height: Breed.height,
    max_height: Breed.height,
    min_weight: Breed.weight,
    max_weight: Breed.weight,
    life_span: "",
    img: "",
    temperaments: [],
  });
  const [temperaments, setTemperaments] = useState();
  const [name] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:3001/temperaments")
      .then((r) => r.data)
      .then((e) => setTemperaments(e));
  }, []);

  const handleSelector = (e) => {
    let temp = Breed.temperaments;
    temp.push(e.target.value);
    // eslint-disable-next-line eqeqeq
    let a = temperaments.filter((i) => i.id == e.target.value);
    name.push(a[0].name);
    setBreed({
      ...Breed,
      [e.target.name]: temp,
    });
    // Breed.temperaments.push(e.target.value)
  };

  const handleChange = (e) => {
    setBreed({
      ...Breed,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className={S.box}>
      <div className={S.container}>
        <form
          onSubmit={(e) => {
            // e.preventDefault()
            axios.post("http://localhost:3001/dog", {
              name: Breed.name,
              min_height: Breed.height,
              max_height: Breed.height,
              min_weight: Breed.weight,
              max_weight: Breed.weight,
              life_span: Breed.life_span,
              image: Breed.img,
              temperaments: Breed.temperaments,
            });
          }}
          className={S.form}
        >
          <input
            required
            name="name"
            type="text"
            placeholder="Name :"
            autoComplete="off"
            onChange={handleChange}
          />
          <div className={S.group}>
            <input
              required
              name="min_weight"
              type="number"
              placeholder="Min Weight :"
              onChange={handleChange}
              min="1"
            />
            <input
              required
              name="max_weight"
              type="number"
              placeholder="Max Weight :"
              onChange={handleChange}
              min="1"
            />
            <input
              required
              name="min_height"
              type="number"
              placeholder="Min Height :"
              onChange={handleChange}
              min="1"
            />
            <input
              required
              name="max_height"
              type="number"
              placeholder="Max Height :"
              onChange={handleChange}
              min="1"
            />
          </div>
          <input
            required
            name="life_span"
            type="number"
            placeholder="Life span :"
            onChange={handleChange}
            min="1"
          />
          <input
            required
            name="img"
            type="link"
            autoComplete="off"
            placeholder="Image Link/Url :"
            onChange={handleChange}
            min="1"
          />
          <select
            className={S.temps}
            onChange={handleSelector}
            name="temperament"
            id="temperaments"
            defaultValue="select"
          >
            <option value="select" hidden disabled>
              select at least one
            </option>
            {temperaments?.map((e) => (
              <option name="temperaments" key={e.id} value={e.id}>
                {e.name}
              </option>
            ))}
          </select>
          <input type="submit"></input>
        </form>

        <ul>
          {name?.map((e) => (
            <li key={Math.random(1)}>{e}</li>
          ))}
        </ul>
      </div>
      <div className={S.imgContainer}>
        <div>Your Breed Image Here</div>
        {Breed.img && (
          // eslint-disable-next-line jsx-a11y/img-redundant-alt
          <img
            className={S.img}
            src={Breed.img}
            alt="If you see this message, the address of your image is invalid, try another"
          />
        )}
      </div>
    </div>
  );
}

export default CreateBreed;
