import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { getBreeds } from "../../Redux/Actions";
import Card  from "../Card/Card";
function Home() {
  const breeds = useSelector((state) => state.breeds);
  const dispatch = useDispatch();
  const itemsPerPage = 10;
  const [data, setData] = React.useState(breeds);
  const [currentPage, setPage] = React.useState(0);

  const nextPage = () => {
    const total = breeds.length;
    const nextPage = currentPage + 1;
    const firstIndex = currentPage + itemsPerPage +1;
    if (firstIndex === total) return;
    setData([...breeds].splice(firstIndex, itemsPerPage));
    setPage(nextPage);
  };

  const prevPage = () => {
    const prev = currentPage - 1;
    if (prev < 0) return;
    const firstIndex = ((prev - 1) * itemsPerPage) +1 
    setData([...breeds].splice(firstIndex, itemsPerPage))
    setPage(prev)
  };

  React.useEffect(() => {
    dispatch(getBreeds());
  }, [dispatch]);

  return (
    <div>
      <Card tems={data}/>
    </div>
  );
}
export default Home;
