import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getBreeds } from "../../Redux/Actions";
import Card from "../Card/Card";
import Pagination from "../Paginator/Paginator";

function Home() {
  const dispatch = useDispatch();
  const breeds = useSelector((state) => state.breeds);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage] = useState(10);

  useEffect(() => {
    dispatch(getBreeds());
  }, [dispatch]);

  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = breeds.slice(indexOfFirstPost, indexOfLastPost);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div>
      <Card breeds={currentPosts} />
      <Pagination postsPerPage={postsPerPage} totalPosts={breeds.lenght} paginate ={paginate}/>
    </div>
  );
}
export default Home;
